#include <bits/stdc++.h>
using namespace std;

class SortEstimate
{
public:
	double howMany(int c, int time);
};

double lg(double n) {
  return log(n) / log(2);
}

double SortEstimate::howMany (int c, int time) 
{
  double l = 1, r = 1e50;

  for(int run = 0; run < (int)1e3; ++run) {
    double mid = (l+r)/2;
    (c*mid*lg(mid)> time? r : l) = mid;
  }
  return l;
}

